('135', '161', 'calculadora de btc', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Calculadora de Bitcoin</title>\r\n    <style>\r\n        /* General Styles */\r\n        body {\r\n            font-family: \'Arial\', sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #0d0d0d;\r\n            color: #ffffff;\r\n        }\r\n\r\n        /* Header */\r\n        header {\r\n            background: linear-gradient(135deg, #003366, #004d40);\r\n            padding: 40px 20px;\r\n            text-align: center;\r\n        }\r\n        header h1 {\r\n            font-size: 2.8em;\r\n            margin: 0;\r\n            color: #00e676;\r\n        }\r\n        header p {\r\n            font-size: 1.2em;\r\n            color: #cfcfcf;\r\n            margin-top: 10px;\r\n        }\r\n\r\n        /* Table Styles */\r\n        table {\r\n            width: 100%;\r\n            max-width: 500px;\r\n            margin: 40px auto;\r\n            border-collapse: collapse;\r\n        }\r\n\r\n        td {\r\n            padding: 10px;\r\n            text-align: center;\r\n        }\r\n\r\n        input[type=\"number\"] {\r\n            width: 100%;\r\n            padding: 10px;\r\n            margin-top: 10px;\r\n            border-radius: 5px;\r\n            border: none;\r\n            background-color: #004d40;\r\n            color: #ffffff;\r\n            font-size: 1em;\r\n        }\r\n\r\n        button {\r\n            padding: 10px 20px;\r\n            background-color: #00e676;\r\n            color: #0d0d0d;\r\n            border: none;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            font-size: 1.2em;\r\n            transition: background-color 0.3s;\r\n        }\r\n\r\n        button:hover {\r\n            background-color: #66ffa6;\r\n        }\r\n\r\n        /* Result Styles */\r\n        #result {\r\n            margin-top: 20px;\r\n            font-size: 1.2em;\r\n            color: #dcdcdc;\r\n        }\r\n\r\n    </style>\r\n</head>\r\n<body>\r\n\r\n<!-- Header -->\r\n<header>\r\n\r\n    <p>Convierte dolares a bitcoin desde aqui</p>\r\n</header>\r\n\r\n<!-- Calculator Table -->\r\n<table>\r\n    <tr>\r\n        <td><label for=\"usdAmount\">Cantidad en USD:</label></td>\r\n        <td><input type=\"number\" id=\"usdAmount\" placeholder=\"Ingresa USD\" /></td>\r\n    </tr>\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <button onclick=\"calculateBitcoin()\">Calcular Bitcoin</button>\r\n        </td>\r\n    </tr>\r\n    <tr>\r\n        <td colspan=\"2\" id=\"result\"></td>\r\n    </tr>\r\n</table>\r\n\r\n<script>\r\n    async function calculateBitcoin() {\r\n        const usdAmount = document.getElementById(\"usdAmount\").value;\r\n        if (usdAmount === \"\" || isNaN(usdAmount) || usdAmount <= 0) {\r\n            alert(\"Por favor, ingresa una cantidad válida en USD.\");\r\n            return;\r\n        }\r\n\r\n        // Obtener el valor actual de Bitcoin desde una API\r\n        try {\r\n            const response = await fetch(\'https://api.coindesk.com/v1/bpi/currentprice/BTC.json\');\r\n            const data = await response.json();\r\n            const btcPrice = data.bpi.USD.rate_float; // Valor de Bitcoin en USD\r\n\r\n            // Calcular la cantidad de Bitcoin\r\n            const btcAmount = usdAmount / btcPrice;\r\n\r\n            // Mostrar el resultado\r\n            document.getElementById(\"result\").innerHTML = `Con ${usdAmount} USD, puedes obtener ${btcAmount.toFixed(6)} BTC.`;\r\n        } catch (error) {\r\n            document.getElementById(\"result\").innerHTML = \"Error al obtener el valor de Bitcoin. Intenta nuevamente.\";\r\n        }\r\n    }\r\n</script>\r\n\r\n</body>\r\n</html>\r\n', '1', 'position2', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '0', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
('136', '163', 'Datos', '', '<div class=\"sidebar\">\r\n    <h3 style=\"background: linear-gradient(to right, #32CD32, #FFD700); -webkit-background-clip: text; color: transparent;\">\r\n    Selecciona un Autor\r\n</h3>\r\n\r\n<p style=\"background: linear-gradient(to right, #1E90FF, #32CD32); -webkit-background-clip: text; color: transparent;\">\r\n    Elige un economista para leer una de sus citas más famosas.\r\n</p>\r\n\r\n    \r\n    <select id=\"authorSelector\" onchange=\"showQuote()\">\r\n        <option value=\"\">Elige un autor</option>\r\n        <option value=\"Mises\">Ludwig von Mises</option>\r\n        <option value=\"Rothbard\">Murray N. Rothbard</option>\r\n        <option value=\"Hayek\">Friedrich Hayek</option>\r\n    </select>\r\n    \r\n    <div id=\"quoteDisplay\" class=\"quoteDisplay\">\r\n        <!-- La cita se mostrará aquí -->\r\n    </div>\r\n</div>\r\n\r\n<script>\r\n    function showQuote() {\r\n        const author = document.getElementById(\'authorSelector\').value;\r\n        const quoteDisplay = document.getElementById(\'quoteDisplay\');\r\n        \r\n        let quote = \"\";\r\n        \r\n        switch (author) {\r\n            case \"Mises\":\r\n                quote = `\"La intervención del gobierno en los mercados no solo distorsiona la economía, sino que también perjudica a aquellos que más necesitan la ayuda.\" - Ludwig von Mises`;\r\n                break;\r\n            case \"Rothbard\":\r\n                quote = `\"El capitalismo no es un sistema de explotación, sino un sistema de cooperación voluntaria.\" - Murray N. Rothbard`;\r\n                break;\r\n            case \"Hayek\":\r\n                quote = `\"El planificador central nunca podrá poseer la información dispersa que está en manos de los individuos en una sociedad libre.\" - Friedrich Hayek`;\r\n                break;\r\n            default:\r\n                quote = \"Selecciona un autor para ver su cita.\";\r\n                break;\r\n        }\r\n        \r\n        quoteDisplay.textContent = quote;\r\n    }\r\n</script>\r\n\r\n<style>\r\n    .sidebar {\r\n        width: 300px;\r\n        background-color: #000000;\r\n        padding: 20px;\r\n        border-radius: 10px;\r\n        box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);\r\n        color: white;\r\n    }\r\n\r\n    .sidebar h3 {\r\n        font-size: 1.5em;\r\n        margin-bottom: 10px;\r\n    }\r\n\r\n    .sidebar p {\r\n        font-size: 0.9em;\r\n        margin-bottom: 15px;\r\n    }\r\n\r\n    .sidebar select {\r\n        width: 100%;\r\n        padding: 10px;\r\n        background-color: #444;\r\n        border: none;\r\n        color: white;\r\n        border-radius: 5px;\r\n        font-size: 0.9em;\r\n    }\r\n\r\n    .sidebar button {\r\n        margin-top: 15px;\r\n        padding: 10px 20px;\r\n        background-color: #007bff;\r\n        color: white;\r\n        border: none;\r\n        border-radius: 5px;\r\n        cursor: pointer;\r\n        font-size: 1em;\r\n    }\r\n\r\n    .sidebar button:hover {\r\n        background-color: #0056b3;\r\n    }\r\n\r\n    .quoteDisplay {\r\n        margin-top: 20px;\r\n        font-size: 1.1em;\r\n        color: #dcdcdc;\r\n        font-style: italic;\r\n        padding: 10px;\r\n        background-color: #444;\r\n        border-radius: 5px;\r\n    }\r\n</style>\r\n', '1', 'position2', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '0', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
