('139', '166', 'Quiz', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>CriptoMundo: Quiz Interactivo</title>\r\n    <style>\r\n        /* General Styles */\r\n        body {\r\n            font-family: \'Arial\', sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #0d0d0d;\r\n            color: #ffffff;\r\n        }\r\n\r\n        /* Quiz Module */\r\n        .quiz-module {\r\n            background: #003366;\r\n            padding: 15px;\r\n            margin: 20px auto;\r\n            max-width: 400px;\r\n            border-radius: 10px;\r\n            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);\r\n            text-align: center;\r\n        }\r\n\r\n        .quiz-module h3 {\r\n            color: #00e676;\r\n            font-size: 1.5em;\r\n            margin-bottom: 10px;\r\n        }\r\n\r\n        .quiz-question {\r\n            margin-bottom: 10px;\r\n            font-size: 1em;\r\n            color: #dcdcdc;\r\n        }\r\n\r\n        .quiz-options {\r\n            list-style: none;\r\n            padding: 0;\r\n            margin: 0;\r\n        }\r\n\r\n        .quiz-options li {\r\n            margin-bottom: 8px;\r\n        }\r\n\r\n        .quiz-options input[type=\"radio\"] {\r\n            margin-right: 8px;\r\n        }\r\n\r\n        .quiz-submit {\r\n            margin-top: 15px;\r\n            padding: 8px 15px;\r\n            background-color: #00e676;\r\n            color: #0d0d0d;\r\n            border: none;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            font-size: 1em;\r\n            transition: background-color 0.3s;\r\n        }\r\n\r\n        .quiz-submit:hover {\r\n            background-color: #66ffa6;\r\n        }\r\n\r\n        .quiz-result {\r\n            margin-top: 10px;\r\n            font-size: 1em;\r\n            color: #dcdcdc;\r\n        }\r\n\r\n    </style>\r\n</head>\r\n<body>\r\n\r\n    <!-- Quiz Module -->\r\n    <div class=\"quiz-module\">\r\n        <h3>Test de Cripto</h3>\r\n\r\n        <div id=\"quiz-container\">\r\n            <div class=\"quiz-question\">\r\n                <p>¿Qué es una criptomoneda?</p>\r\n                <ul class=\"quiz-options\">\r\n                    <li><input type=\"radio\" name=\"q1\" value=\"a\"> Una moneda digital sin banco central.</li>\r\n                    <li><input type=\"radio\" name=\"q1\" value=\"b\"> Una moneda física para pagos.</li>\r\n                    <li><input type=\"radio\" name=\"q1\" value=\"c\"> Solo un sistema de pago online.</li>\r\n                </ul>\r\n            </div>\r\n\r\n            <div class=\"quiz-question\">\r\n                <p>¿Qué es blockchain?</p>\r\n                <ul class=\"quiz-options\">\r\n                    <li><input type=\"radio\" name=\"q2\" value=\"a\"> Un servidor para criptomonedas.</li>\r\n                    <li><input type=\"radio\" name=\"q2\" value=\"b\"> Red descentralizada para transacciones seguras.</li>\r\n                    <li><input type=\"radio\" name=\"q2\" value=\"c\"> Un software para minar monedas.</li>\r\n                </ul>\r\n            </div>\r\n\r\n            <button class=\"quiz-submit\" onclick=\"submitQuiz()\">Enviar</button>\r\n\r\n            <div id=\"quiz-result\" class=\"quiz-result\" style=\"display:none;\">\r\n                <p>¡Gracias! Verifica tus respuestas.</p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n\r\n    <script>\r\n        function submitQuiz() {\r\n            var correctAnswers = 0;\r\n\r\n            // Check question 1\r\n            var q1 = document.querySelector(\'input[name=\"q1\"]:checked\');\r\n            if (q1 && q1.value === \"a\") {\r\n                correctAnswers++;\r\n            }\r\n\r\n            // Check question 2\r\n            var q2 = document.querySelector(\'input[name=\"q2\"]:checked\');\r\n            if (q2 && q2.value === \"b\") {\r\n                correctAnswers++;\r\n            }\r\n\r\n            // Display result\r\n            var resultText = \"Respondiste correctamente a \" + correctAnswers + \" de 2 preguntas.\";\r\n            var resultElement = document.getElementById(\"quiz-result\");\r\n            resultElement.innerHTML = \"<p>\" + resultText + \"</p>\";\r\n            resultElement.style.display = \"block\";\r\n        }\r\n    </script>\r\n\r\n</body>\r\n</html>\r\n', '1', 'position2', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '0', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
