('141', '168', 'Ideas para realizar tus propios proyectos', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Generador de Proyectos Cripto</title>\r\n    <style>\r\n        /* General Styles */\r\n        body {\r\n            font-family: \'Arial\', sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #0d0d0d;\r\n            color: #ffffff;\r\n        }\r\n\r\n        /* Crypto Project Generator */\r\n        .project-generator {\r\n            background: #003366;\r\n            padding: 15px;\r\n            margin: 20px auto;\r\n            max-width: 100%;\r\n            width: 100%;\r\n            border-radius: 10px;\r\n            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);\r\n            text-align: center;\r\n            display: flex;\r\n            justify-content: center;\r\n            flex-direction: column;\r\n        }\r\n\r\n        .project-generator h3 {\r\n            color: #00e676;\r\n            font-size: 1.5em;\r\n            margin-bottom: 10px;\r\n        }\r\n\r\n        .project-generator .project-name {\r\n            font-size: 1.3em;\r\n            font-weight: bold;\r\n            color: #ff9800;\r\n            margin-bottom: 15px;\r\n        }\r\n\r\n        .project-generator .project-description {\r\n            font-size: 1em;\r\n            color: #dcdcdc;\r\n            margin-bottom: 20px;\r\n        }\r\n\r\n        .generate-button {\r\n            padding: 10px 20px;\r\n            background-color: #00e676;\r\n            color: #0d0d0d;\r\n            border: none;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            font-size: 1.1em;\r\n            transition: background-color 0.3s;\r\n        }\r\n\r\n        .generate-button:hover {\r\n            background-color: #66ffa6;\r\n        }\r\n\r\n    </style>\r\n</head>\r\n<body>\r\n\r\n    <!-- Crypto Project Generator -->\r\n    <div class=\"project-generator\">\r\n        <h3>Generador de Ideas de Proyectos Cripto</h3>\r\n        <div class=\"project-name\" id=\"project-name\">Generando...</div>\r\n        <div class=\"project-description\" id=\"project-description\">Generando...</div>\r\n        <button class=\"generate-button\" onclick=\"generateProject()\">Generar Proyecto</button>\r\n    </div>\r\n\r\n    <script>\r\n        // Ideas de proyectos y descripciones\r\n        const projectIdeas = [\r\n            {\r\n                name: \"CryptoPay\",\r\n                description: \"Plataforma de pagos en criptomonedas para comercios locales, con integración de múltiples monedas y opciones de conversión instantánea.\"\r\n            },\r\n            {\r\n                name: \"DeFi Health\",\r\n                description: \"Aplicación descentralizada para la gestión de seguros de salud, utilizando smart contracts para facilitar la transparencia y los pagos rápidos.\"\r\n            },\r\n            {\r\n                name: \"GreenCoin\",\r\n                description: \"Criptomoneda ecológica que recompensa a los usuarios por acciones sostenibles, como reciclar o utilizar energía renovable.\"\r\n            },\r\n            {\r\n                name: \"NFT Art Gallery\",\r\n                description: \"Galería virtual para coleccionistas de arte NFT, con subastas en tiempo real y la posibilidad de comprar, vender e intercambiar obras digitales.\"\r\n            },\r\n            {\r\n                name: \"CryptoVote\",\r\n                description: \"Sistema de votación descentralizado basado en blockchain para elecciones seguras y transparentes, con verificación en tiempo real.\"\r\n            },\r\n            {\r\n                name: \"ChainSecure\",\r\n                description: \"Plataforma de seguridad para criptomonedas que permite a los usuarios asegurar sus activos mediante contratos inteligentes y autenticación de múltiples factores.\"\r\n            },\r\n            {\r\n                name: \"EduCoin\",\r\n                description: \"Criptomoneda dedicada a la educación, permitiendo a los estudiantes ganar tokens por completar cursos y participar en actividades educativas.\"\r\n            },\r\n            {\r\n                name: \"SmartRent\",\r\n                description: \"Plataforma para alquiler de propiedades basada en blockchain, donde los pagos se realizan mediante criptomonedas y los contratos son inteligentes.\"\r\n            },\r\n            {\r\n                name: \"CryptoLoan\",\r\n                description: \"Plataforma de préstamos P2P con criptomonedas, donde los usuarios pueden pedir prestado o prestar fondos con tasas de interés más bajas.\"\r\n            },\r\n            {\r\n                name: \"Blockchain Gaming Hub\",\r\n                description: \"Plataforma para juegos basados en blockchain, donde los jugadores pueden ganar tokens y NFT mientras participan en competiciones y desafíos.\"\r\n            },\r\n        {\r\n        name: \"CryptoArt Marketplace\",\r\n        description: \"Un mercado descentralizado donde los artistas pueden vender sus obras digitales como NFT, permitiendo la compra y venta segura con criptomonedas.\"\r\n    },\r\n    {\r\n        name: \"Play2Earn Network\",\r\n        description: \"Plataforma de juegos donde los jugadores ganan criptomonedas y NFT al completar misiones y alcanzar logros dentro del juego.\"\r\n    },\r\n    {\r\n        name: \"NFT Fashion\",\r\n        description: \"Colección y compra de ropa digital en formato NFT, permitiendo a los usuarios personalizar avatares en mundos virtuales o redes sociales.\"\r\n    },\r\n    {\r\n        name: \"CryptoCrowdfunding\",\r\n        description: \"Plataforma de financiamiento colectivo basada en blockchain, donde los proyectos se financian mediante criptomonedas y se distribuyen tokens a los inversores.\"\r\n    },\r\n    {\r\n        name: \"Metaverse Real Estate\",\r\n        description: \"Mercado de bienes raíces virtuales en el metaverso, donde los usuarios compran, venden y alquilan propiedades digitales usando criptomonedas.\"\r\n    },\r\n    {\r\n        name: \"DAO Music Platform\",\r\n        description: \"Plataforma musical descentralizada donde los artistas pueden distribuir su música y recibir pagos directamente de los oyentes en criptomonedas.\"\r\n    },\r\n    {\r\n        name: \"Crypto Charity\",\r\n        description: \"Plataforma de donaciones benéficas basada en blockchain, que permite a los usuarios donar criptomonedas directamente a causas transparentes y verificadas.\"\r\n    }\r\n];\r\n\r\n        // Función para generar un proyecto aleatorio\r\n        function generateProject() {\r\n            const randomIndex = Math.floor(Math.random() * projectIdeas.length);\r\n            const project = projectIdeas[randomIndex];\r\n\r\n            // Actualizar el nombre y la descripción del proyecto\r\n            document.getElementById(\"project-name\").textContent = project.name;\r\n            document.getElementById(\"project-description\").textContent = project.description;\r\n        }\r\n    </script>\r\n\r\n</body>\r\n</html>\r\n', '1', 'bottom1', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '1', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
