to\'; // Enlace al artículo \"El Misterio de Satoshi Nakamoto\"\r\n    }\r\n  }\r\n</script>\r\n', '1', 'bottom2', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '1', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
('147', '174', 'Encuesta', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Quiz de Economía</title>\r\n    <style>\r\n        /* General Styles */\r\n        body {\r\n            font-family: \'Arial\', sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #0d0d0d;\r\n            color: #ffffff;\r\n        }\r\n\r\n        /* Quiz Container */\r\n        .quiz-container {\r\n            width: 80%;\r\n            max-width: 600px;\r\n            margin: 50px auto;\r\n            background-color: #003366;\r\n            padding: 20px;\r\n            border-radius: 10px;\r\n            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);\r\n        }\r\n\r\n        h2 {\r\n            text-align: center;\r\n            color: #00e676;\r\n        }\r\n\r\n        .question {\r\n            font-size: 1.2em;\r\n            margin-bottom: 20px;\r\n        }\r\n\r\n        .options {\r\n            list-style-type: none;\r\n            padding: 0;\r\n        }\r\n\r\n        .options li {\r\n            background-color: #004d40;\r\n            padding: 10px;\r\n            margin: 5px 0;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            transition: background-color 0.3s;\r\n        }\r\n\r\n        .options li:hover {\r\n            background-color: #00e676;\r\n        }\r\n\r\n        .result {\r\n            text-align: center;\r\n            font-size: 1.5em;\r\n            margin-top: 20px;\r\n        }\r\n\r\n        .btn {\r\n            background-color: #00e676;\r\n            color: #0d0d0d;\r\n            padding: 10px 20px;\r\n            border: none;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            font-size: 1.2em;\r\n            margin-top: 20px;\r\n            display: block;\r\n            width: 100%;\r\n            text-align: center;\r\n        }\r\n\r\n        .btn:hover {\r\n            background-color: #66ffa6;\r\n        }\r\n\r\n        .reset-btn {\r\n            background-color: #ff4444;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n\r\n<div class=\"quiz-container\">\r\n    <h2>Quiz de Economía</h2>\r\n    <div id=\"question-container\">\r\n        <div class=\"question\" id=\"question-text\"></div>\r\n        <ul class=\"options\" id=\"options-list\"></ul>\r\n    </div>\r\n    <div class=\"result\" id=\"result-container\"></div>\r\n    <button class=\"btn reset-btn\" id=\"reset-btn\" style=\"display: none;\" onclick=\"resetQuiz()\">Reiniciar Quiz</button>\r\n</div>\r\n\r\n<script>\r\n    // Preguntas del quiz\r\n    const questions = [\r\n        {\r\n            question: \"¿Qué es el PIB (Producto Interno Bruto)?\",\r\n            options: [\r\n                \"La cantidad de dinero que tiene un país en su banco central\",\r\n                \"El valor total de todos los bienes y servicios producidos por un país en un año\",\r\n                \"El total de las importaciones y exportaciones de un país\",\r\n                \"La cantidad de dinero que un país gasta en defensa\"\r\n            ],\r\n            correctAnswer: 1\r\n        },\r\n        {\r\n            question: \"¿Qué es la inflación?\",\r\n            options: [\r\n                \"El aumento de la producción de bienes y servicios\",\r\n                \"La disminución de la tasa de interés\",\r\n                \"El aumento generalizado y sostenido de los precios de los bienes y servicios\",\r\n                \"La reducción de la deuda pública\"\r\n            ],\r\n            correctAnswer: 2\r\n        },\r\n        {\r\n            question: \"¿Qué es la Ley de Oferta y Demanda?\",\r\n            options: [\r\n                \"La teoría que explica cómo los gobiernos pueden controlar los precios\",\r\n                \"La teoría que establece que el precio de un bien depende de la cantidad disponible y la demanda\",\r\n                \"Una ley que prohíbe el comercio entre países\",\r\n                \"Una teoría económica que solo aplica a las criptomonedas\"\r\n            ],\r\n            correctAnswer: 1\r\n        },\r\n        {\r\n            question: \"¿Qué significa la tasa de interés negativa?\",\r\n            options: [\r\n                \"Que los bancos cobran intereses por depositar dinero\",\r\n                \"Que los consumidores tienen que pagar más por los préstamos\",\r\n                \"Que los bancos deben pagar a los consumidores por mantener su dinero en el banco\",\r\n                \"Que los préstamos se vuelven gratuitos\"\r\n            ],\r\n            correctAnswer: 2\r\n        },\r\n        {\r\n            question: \"¿Cuál es la principal diferencia entre una acción y un bono?\",\r\n            options: [\r\n                \"Una acción es un préstamo, mientras que un bono es una participación en la propiedad\",\r\n                \"Una acción representa propiedad en una empresa, mientras que un bono es un préstamo a la empresa\",\r\n                \"Ambos son préstamos, pero un bono es a corto plazo\",\r\n                \"Ambos representan propiedad en una empresa\"\r\n            ],\r\n            correctAnswer: 1\r\n        }\r\n    ];\r\n\r\n    let currentQuestionIndex = 0;\r\n    let score = 0;\r\n\r\n    // Mostrar pregunta y opciones\r\n    function showQuestion() {\r\n        const question = questions[currentQuestionIndex];\r\n        document.getElementById(\"question-text\").textContent = question.question;\r\n\r\n        const optionsList = document.getElementById(\"options-list\");\r\n        optionsList.innerHTML = \"\";\r\n\r\n        question.options.forEach((option, index) => {\r\n            const li = document.createElement(\"li\");\r\n            li.textContent = option;\r\n            li.onclick = () => checkAnswer(index);\r\n            optionsList.appendChild(li);\r\n        });\r\n    }\r\n\r\n    // Verificar respuesta\r\n    function checkAnswer(selectedIndex) {\r\n        const correctAnswer = questions[currentQuestionIndex].correctAnswer;\r\n        if (selectedIndex === correctAnswer) {\r\n            score++;\r\n        }\r\n\r\n        currentQuestionIndex++;\r\n\r\n        if (currentQuestionIndex < questions.length) {\r\n            showQuestion();\r\n        } else {\r\n            showResult();\r\n        }\r\n    }\r\n\r\n    // Mostrar resultado\r\n    function showResult() {\r\n        document.getElementById(\"question-container\").style.display = \"none\";\r\n        document.getElementById(\"result-container\").style.display = \"block\";\r\n        document.getElementById(\"result-container\").textContent = `¡Has terminado! Tu puntaje es: ${score} de ${questions.length}`;\r\n\r\n        document.getElementById(\"reset-btn\").style.display = \"block\";\r\n    }\r\n\r\n    // Reiniciar quiz\r\n    function resetQuiz() {\r\n        currentQuestionIndex = 0;\r\n        score = 0;\r\n        document.getElementById(\"question-container\").style.display = \"block\";\r\n        document.getElementById(\"result-container\").style.display = \"none\";\r\n        document.getElementById(\"reset-btn\").style.display = \"none\";\r\n        showQuestion();\r\n    }\r\n\r\n    // Iniciar quiz\r\n    showQuestion();\r\n</script>\r\n\r\n</body>\r\n</html>\r\n', '1', 'bottom1', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '0', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
('148', '176', 'Descifra el bloque', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Módulo Interactivo: Descifra el Bloque Génesis</title>\r\n    <style>\r\n        body {\r\n            font-family: \'Arial\', sans-serif;\r\n            margin: 0;\r\n            padding: 20px;\r\n            background-color: #0d0d0d;\r\n            color: #ffffff;\r\n        }\r\n\r\n        .module {\r\n            background-color: #004d40;\r\n            padding: 30px;\r\n            border-radius: 10px;\r\n            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);\r\n            text-align: center;\r\n            max-width: 600px;\r\n            margin: 20px auto;\r\n        }\r\n\r\n        h2 {\r\n            color: #00e676;\r\n            margin-bottom: 20px;\r\n        }\r\n\r\n        .input-box {\r\n            padding: 10px;\r\n            margin-bottom: 15px;\r\n            width: 80%;\r\n            border: none;\r\n            border-radius: 5px;\r\n            font-size: 1em;\r\n        }\r\n\r\n        .btn {\r\n            padding: 10px 20px;\r\n            background-color: #00e676;\r\n            color: #0d0d0d;\r\n            border: none;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            font-size: 1em;\r\n        }\r\n\r\n        .btn:hover {\r\n            background-color: #66ffa6;\r\n        }\r\n\r\n        .message {\r\n            margin-top: 15px;\r\n            font-size: 1em;\r\n            color: #dcdcdc;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n    <div class=\"module\">\r\n        <h2>Descifra el Mensaje del Bloque Génesis</h2>\r\n        <p>Introduce una fecha y descubre si está relacionada con algún evento histórico en blockchain.</p>\r\n        <input type=\"text\" id=\"dateInput\" class=\"input-box\" placeholder=\"Ejemplo: 03/01/2009\">\r\n        <button class=\"btn\" onclick=\"checkGenesisDate()\">Verificar</button>\r\n        <div id=\"message\" class=\"message\"></div>\r\n    </div>\r\n\r\n    <script>\r\n        function checkGenesisDate() {\r\n            const dateInput = document.getElementById(\'dateInput\').value.trim();\r\n            const messageBox = document.getElementById(\'message\');\r\n\r\n            if (dateInput === \"03/01/2009\") {\r\n                messageBox.innerHTML = \"<strong>Correcto:</strong> El 3 de enero de 2009, Satoshi Nakamoto creó el bloque génesis de Bitcoin, marcando el inicio de la revolución blockchain.\";\r\n            } else if (dateInput) {\r\n                messageBox.innerHTML = \"Lo siento, esa fecha no está relacionada con el bloque génesis de Bitcoin. Intenta otra vez.\";\r\n            } else {\r\n                messageBox.innerHTML = \"Por favor, introduce una fecha para continuar.\";\r\n            }\r\n        }\r\n    </script>\r\n</body>\r\n</html>\r\n', '1', 'position2', NULL, NULL, NULL, NULL, '1', 'mod_custom', '1', '0', '{\"prepare_content\":0,\"backgroundimage\":\"\",\"layout\":\"_:default\",\"moduleclass_sfx\":\"\",\"cache\":1,\"cache_time\":900,\"cachemode\":\"static\",\"module_tag\":\"div\",\"bootstrap_size\":\"0\",\"header_tag\":\"h3\",\"header_class\":\"\",\"style\":\"0\"}', '0', '*'),
