('147', '174', 'Encuesta', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Quiz de Econom√≠a</title>\r\n    <style>\r\n        /* General Styles */\r\n        body {\r\n            font-family: \'Arial\', sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #0d0d0d;\r\n            color: #ffffff;\r\n        }\r\n\r\n        /* Quiz Container */\r\n        .quiz-container {\r\n            width: 80%;\r\n            max-width: 600px;\r\n            margin: 50px auto;\r\n            background-color: #003366;\r\n            padding: 20px;\r\n            border-radius: 10px;\r\n            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);\r\n        }\r\n\r\n        h2 {\r\n            text-align: center;\r\n            color: #00e676;\r\n        }\r\n\r\n        .question {\r\n            font-size: 1.2em;\r\n            margin-bottom: 20px;\r\n        }\r\n\r\n        .options {\r\n            list-style-type: none;\r\n            padding: 0;\r\n        }\r\n\r\n        .options li {\r\n            background-color: #004d40;\r\n            padding: 10px;\r\n            margin: 5px 0;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            transition: background-color 0.3s;\r\n        }\r\n\r\n        .options li:hover {\r\n            background-color: #00e676;\r\n        }\r\n\r\n        .result {\r\n            text-align: center;\r\n            font-size: 1.5em;\r\n            margin-top: 20px;\r\n        }\r\n\r\n        .btn {\r\n            background-color: #00e676;\r\n            color: #0d0d0d;\r\n            padding: 10px 20px;\r\n            border: none;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            font-size: 1.2em;\r\n            margin-top: 20px;\r\n            display: block;\r\n            width: 100%;\r\n            text-