('165', '199', 'bottom 1 arte trading', '', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Crypto Quiz</title>\r\n    <style>\r\n        body {\r\n            font-family: Arial, sans-serif;\r\n            background: #1a1a1a;\r\n            color: #dcdcdc;\r\n            margin: 0;\r\n            padding: 0;\r\n            text-align: center;\r\n        }\r\n        .crypto-quiz-module {\r\n            padding: 15px;\r\n            background: linear-gradient(to right, #1a1a1a, #0d0d0d);\r\n            border: 2px solid #00e676;\r\n            border-radius: 8px;\r\n            color: #dcdcdc;\r\n            text-align: center;\r\n            margin-top: 50px;\r\n        }\r\n        button {\r\n            background: #00e676;\r\n            color: #1a1a1a;\r\n            border: none;\r\n            padding: 10px 20px;\r\n            font-size: 1em;\r\n            border-radius: 5px;\r\n            cursor: pointer;\r\n            margin: 5px;\r\n            transition: transform 0.2s ease-in-out;\r\n        }\r\n        button:hover {\r\n            transform: scale(1.05);\r\n            background: #2196f3;\r\n        }\r\n        .question {\r\n            font-size: 1.2em;\r\n            color: #ffffff;\r\n        }\r\n        .feedback {\r\n            font-weight: bold;\r\n            margin-top: 10px;\r\n        }\r\n        .feedback.correct {\r\n            color: #00e676;\r\n        }\r\n        .feedback.incorrect {\r\n            color: #ff0000;\r\n        }\r\n        .options {\r\n            margin-top: 10px;\r\n        }\r\n        .options button {\r\n            margin: 5px;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n\r\n<!-- Crypto Quiz Module -->\r\n<div class=\"crypto-quiz-module\">\r\n    <!-- Start Button -->\r\n    <button id=\"startQuiz\" onclick=\"startQuiz()\">Iniciar Quiz</button>\r\n\r\n    <!-- Quiz Content (Initially Hidden) -->\r\n    <div id=\"quizContent\" style=\"display:none;\">\r\n        <!-- Question 1 -->\r\n        <p id=\"question1\" class=\"question\">Pregunta 1: ¬øQu√© altcoin es conocida por su velocidad y bajos costos?</p>\r\n        <div id=\"q1Options\" class=\"options\">\r\n            <button onclick=\"checkAnswer(1, \'B\')\">B) Solana</button>\r\n            <button onclick=\"checkAnswer(1, \'A\')\">A) Bitcoin</button>\r\n            <button onclick=\"checkAnswer(1, \'C\')\">C) Ethereum</button>\r\n        </div>\r\n\r\n        <!-- Feedback for answers -->\r\n        <p id=\"feedback\" class=\"feedback\" style=\"display:none;\"></p>\r\n\r\n        <!-- Next Question Button -->\r\n        <button id=\"nextBtn\" onclick=\"nextQuestion()\" style=\"display:none;\">Siguiente Pregunta</button>\r\n    </div>\r\n</div>\r\n\r\n<!-- Script -->\r\n<script>\r\n    const questions = [\r\n        {\r\n            question: \"¬øQu√© altcoin es conocida por su velocidad y bajos costos?\",\r\n            options: [\"A) Bitcoin\", \"B) Solana\", \"C) Ethereum\"],\r\n            correctAnswer: \"B\"\r\n        },\r\n        {\r\n            question: \"¬øQu√© criptomoneda es famosa por su capacidad de ejecutar contratos inteligentes?\",\r\n            options: [\"A) Dogecoin\", \"B) Ethereum\", \"C) Litecoin\"],\r\n            correctAnswer: \"B\"\r\n        },\r\n        {\r\n            question: \"¬øCu√°l es el protocolo de consenso que usa Bitcoin?\",\r\n            options: [\"A) Proof of Stake\", \"B) Proof of Work\", \"C) Delegated Proof of Stake\"],\r\n            correctAnswer: \"B\"\r\n        },\r\n        {\r\n            question: \"¬øQu√© significa la \'dominancia de Bitcoin\' en el mercado?\",\r\n            options: [\"A) La cantidad de Bitcoin en circulaci√≥n\", \"B) El precio m√°s alto alcanzado por Bitcoin\", \"C) El porcentaje de la capitalizaci√≥n total del mercado representado por Bitcoin\"],\r\n            correctAnswer: \"C\"\r\n  